label("$D$",(0,0),SW);
label("$E$",(8.5,-10/7),S);
draw((2,0)--(3,0),Arrow);
draw((3,0)--(4,0),Arrow);
draw((2,5)--(3,5),Arrow);
label("$x^\circ$",(0.1,4));
draw((3,5)--(4,5),Arrow);
label("$115^\circ$",(0,0),NE);
label("$75^\circ$",(8,5),SW);
label("$105^\circ$",(6.5,0),E);
[/asy]

