}

path foo = moo(0);
real t;

for (t = 0; t <= pi + 0.1; t = t + 0.1) {
  foo = foo--moo(t);
}

draw(foo,red);

draw((-0.5,0)--(1.5,0));
draw((0,-0.5)--(0,0.5));
label("$r = \cos \theta$", (1.3,0.4), red);
[/asy]

