B = (0,0);
C = (8,0);
A = intersectionpoint(arc(B,7,0,180),arc(C,9,0,180));
P = interp(A,B,(12 - 3*sqrt(2))/2/7);
Q = interp(A,C,(12 + 3*sqrt(2))/2/9);
R = interp(C,A,6/9);
S = interp(C,B,6/8);
X = extension(P,Q,R,S);
Y = (P + reflect(A,C)*(P))/2;

draw(A--B--C--cycle);
draw(P--Y);
draw(P--Q);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
