A = (2,5);
B = (0,0);
C = (8,0);
D = (A + reflect(B,C)*(A))/2;

draw(A--D);
draw((0,0)--(8,0));

dot("$\mathbf{a}$", A, N);
dot("$\mathbf{v}$", D, S);
