draw(Circle((0,-1),sqrt(2)),red);
draw((-3,0)--(3,0));
draw((0,-3)--(0,3));

label("Re", (3,0), E);
label("Im", (0,3), N);

dot("$i$", (0,1), E);
dot("$-i$", (0,-1), E);
[/asy]

